const ADD_TODO = 'ADD_TODO '


const initialState = {
	todos: []
};

const todos = (state = initialState, action) => {
    switch (action.type) {
        case ADD_TODO:
            return [
                {
                    id: action.id,
                    text: action.payload,
                    completed: false
                },
                ...state.todos,

            ];
        default:
            return state
    }
}

  console.log(action.payload)
  export default todos
