const ADD_TODO = 'ADD_TODO '

const initialState = {
	todos: []
};

const todos = (state = initialState, action) => {
console.log(action.payload)

    switch (action.type) {
        case ADD_TODO:
            return [
                {
                    id: action.id,
                    text: action.payload,
                    completed: false
                },
                ...state.todos,

            ];
        default:
            return state
    }
}

  export default todos
