const ADD_TODO = 'ADD_TODO '

const initialState = {
	todos: []
};

const todos = (state = initialState, action) => {

    switch (action.type) {
        case ADD_TODO:
            return [
                ...state.todos,
                {
                    id: action.id,
                    text: action.payload,
                    completed: false
                },


            ];
        default:
            return state
    }
}

  export default todos
