import { v4 as uuidv4 } from 'uuid';
import { ADD_TODO } from '../reducers/todos';

export const addTodo = (text) => {
    return (dispatch) => {
        return dispatch({
            type: ADD_TODO ,
            id: uuidv4(),
            text,
            completed: false})
    }
}
